package com.example1;

public class ExploringException {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int numerator = 100;
		int denominator = 5;
		
		while (denominator >= 0) {
			 int result = numerator / denominator;
			 
			 System.out.println(result);
			 
			 denominator--;
			 
		}

	}

}
